function eventListeners() {
    document.querySelector(".mobile-menu").addEventListener("click", navegacionResponsive)

    //Muestra campos condicionales
    const metodoContacto = document.querySelectorAll('input[name="contacto[contacto]"]');
    metodoContacto.forEach(input => input.addEventListener('click', mostrarMetodosContacto));

}

function navegacionResponsive() {
    const e = document.querySelector(".navegacion");
    e.classList.contains("mostrar") ? e.classList.remove("mostrar") : e.classList.add("mostrar")
}


function mostrarMetodosContacto(e) {
    const contactoDiv = document.querySelector('#contacto');

    if (e.target.value == 'telefono') {
        contactoDiv.innerHTML = `<label for="telefono"></label>
            <input type="tel" placeholder="Tu teléfono" id="telefono" required name="contacto[telefono]">

            <label for="fecha">Cuándo desea ser contactado(a):</label>
            <input type="date" id="fecha" name="contacto[fecha]">

            <label for="hora">Hora:</label>
            <input type="time" id="hora" min="09:00" max="18:00" name="contacto[hora]">`;
    } else {
        contactoDiv.innerHTML =
            `<label for=" email"></label>
            <input type="email" placeholder="Tu Email" id="email" required name="contacto[email]"> `;
    }

}


